package com.andersostby.webhook

import io.ktor.util.getDigestFunction
import org.junit.jupiter.api.Assertions.assertTrue
import org.junit.jupiter.api.Test
import java.security.MessageDigest
import java.util.*

internal class MainTest {
    @Test
    internal fun hash() {
        val hash = getDigestFunction("SHA1") { secret }(json)
        hash.contentToString()
        val hashBase64 = Base64.getEncoder().encodeToString(hash)
        println(hashString(hash))
        println(hashToString(hash))
    }

    private fun hashString(bytes: ByteArray): String {
        val HEX_CHARS = "0123456789abcdef"
        val result = StringBuilder(bytes.size * 2)

        bytes.forEach {
            val i = it.toInt()
            result.append(HEX_CHARS[i shr 4 and 0x0f])
            result.append(HEX_CHARS[i and 0x0f])
        }

        return result.toString()
    }

    private fun hashToString(hash: ByteArray): String {
        return hash.fold("", { str, it -> str + "%02x".format(it) })
    }
}

